sender_address = '0x0835D4a1494905fb3f9fEa60ADcbb937ED2fEbf3'
sender_checksum_address = Web3.toChecksumAddress(sender_address)

contract = web3.eth.contract(address = contract_checksum_address, abi = contract_abi)

def getBalance():
  result = contract.functions.balanceOf(sender_checksum_address).call()
  format = web3.fromWei(result, 'ether')
  print(format)

getBalance()